INSERT INTO `categories` (`id`, `name`, `description`, `type`, `has_parent`, `has_child`, `cat_icon`, `cat_background`, `active`, `order`, `created_at`, `updated_at`, `parents`) VALUES
                                                                                                                                                                                      (3, 'Dịch vụ vé máy bay và xe du lịch', 'Chúng tôi với đội ngũ lái xe được tuyển chọn và đào tạo bài bản, chuyên nghiệp, lịch thiệp đặt trách nhiệm an toàn lên hàng đầu. Dàn xe đời mới, chất lượng cao của các hãng xe có thương hiệu toàn cầu chắc chắn sẽ làm hài lòng Quý khách hàng.\r\nVui lòng liê', 3, 1, 1, '2016_09_07_8fb910533e.jpeg', '', 1, NULL, '2016-03-12 04:34:35', '2016-09-12 02:39:17', 0),
                                                                                                                                                                                      (4, 'Khách sạn', '', 3, 0, NULL, NULL, '', 1, NULL, '2016-03-12 04:34:42', '2016-03-12 04:34:42', 0),
                                                                                                                                                                                      (5, 'Cẩm nang du lịch', '', 3, 0, NULL, NULL, '', 1, NULL, '2022-04-01 01:47:23', '2022-04-01 01:47:23', 0);
